 return {
 products : [products , setProducts],
 callback : [callback , setCallback],
        category: [category, setCategory],
  result: [result, setResult],
  sort : [ sort  , setSort],
  search : [ search  , setSearch],
  page : [ page   , setPage]  
 }
 }

export default ProductaAPI
