import React from 'react'
// import {Link} from 'react-router-dom'
import BtnRender from './BtnRender'
import axios from 'axios'

function ProductItem({product,isAdmin,handleCheck , token , callback ,setCallback}) {

 const deleteProduct = async()=>{
  // console.log(product)
  try {
    const destroyImg =  axios.post('/api/destroy',{public_id : product.images.public_id},{
     headers : {Authorization : token}
    })
    const deleteProduct = await axios.delete(`/api/products/${product._id}`,{
     headers : {Authorization : token}
    })
    await destroyImg
    await deleteProduct
    setCallback(!callback)
  } catch (error) {
   alert(error.response.data.msg)
  }
 }
 return (
  <div className="product_card">
   {
    isAdmin && <input type="checkbox"  checked={product.checked} onChange={() => handleCheck(product._id)}/>
   }
   <img src={product.images.url} alt=""  />

   <div className="product_box">
    <h1 title={product.title}>{product.title}</h1>
    <span>${product.price}</span>
    <p>{product.description}</p>
   </div>

  
   <BtnRender product={product} deleteProduct={deleteProduct}/>
  </div>
 )
}

export default ProductItem
