import React, { useContext, useEffect, useState } from 'react';
import {useParams,Link} from 'react-router-dom';
import {GlobalState} from '../../../GlobalState';
import ProductItem from '../utils/productItem/ProductItem'

function DetailProduct() {
 const param = useParams()
 const state = useContext(GlobalState)
 const [products] = state.productsAPI.products
 const [detailProduct , setDetailProduct] = useState([])
 const addCart = state.UserAPI.addCart

useEffect(()=>{
  // console.log('re render') 
  if(param.id){
   products.forEach(product=>{
    if(product._id === param.id) setDetailProduct(product)
   })
  }
},[param,products])

 // console.log(detailProduct)
 if(detailProduct.length === 0) return null;
 return (
  <>
  <div className="container detail" style={{padding: '0rem 11rem'}}>
   <img src={detailProduct.images.url} alt="" srcset="" style={{width:'100%',marginLeft:'3%'}} />
   <div className="box-detail" >
    <div className="row">
     <h2>{detailProduct.title}</h2>
     <h6>#id: {detailProduct.product_id}</h6>
    </div>
    <span>${detailProduct.price}</span>
    <p>{detailProduct.description}</p>
    <p>{detailProduct.content}</p>
    <p>${detailProduct.sold}</p>
    <Link to="/cart" className="cart"
    onClick={()=>{
        addCart(detailProduct)
    }}>Buy Now</Link>
   </div>
  </div>

  <div className="container" style={{textAlign:'center'}}>
   <h2>Related products</h2>
   <div className="products" style={{display:'flex',justifyContent:'space-between',    padding: '0rem 15rem'}}>
    {
     products.map(product=>{
      return product.category === detailProduct.category
      ? <ProductItem key={product._id} product={product}  /> : null
     })
    }
   </div>
  </div>
  </>
  
 )
}

export default DetailProduct
