import React, { useState } from 'react'
import {Link} from 'react-router-dom';
import axios from 'axios'
function Login() {
 const [user , setUser] = useState({
  email : "",
  password : ""
 })

 const onChangeInput = e=>{
  const {name,value} = e.target;
  setUser({...user,[name]:value})
 }

 const loginSubmit=async e=>{
  e.preventDefault()
  try {
   await axios.post("/user/login",{...user})
   localStorage.setItem('firstLogin' ,true )

   // localStorage.setItem("firstLogin", true)

   window.location.href = "/"
  } catch (error) {
   alert(error.response.data.msg)
  }
 }
 return (
   <>

   <section className="row">
    <section className="child">
     <button type="submit">Company</button>
     <button type="submit">Associates</button>
     <form action="">
      <label htmlFor="">email</label>
      <input type="text" placeholder="Enter your email address" /><br />
      <input type="text" placeholder="Enter your password" />
     </form>
    </section>
    <section className="child">
     <img src="https://www.carriermanagement.com/assets/bigstock-world-Map-Best-Concept-of-glo-62830309-connected-global-globalization-interconnected-world.jpg" alt="" srcset="" />
    </section>
   </section>
   
