import React, { useState } from 'react'

function Deals() {
 
 const colorw = "white";
 const [ bg , setBg] = useState(colorw);
 const [name , setName] = useState("Buying")
 const colorAdj=()=>{
  // console.log('clicked')
  let newBg = "blue"
  setBg(newBg)
  setName(<span style={{color:'white'}}>"Buying"</span>)
 }

 return (
  <div >
   <button className="button-adj" onClick={colorAdj} style={{backgroundColor:bg , color:'#0071DF'}}
   >{name}</button>
   <button  className="button-adj1">Selling</button>
  </div>
 )
}

export default Deals
