import React, { useContext, useState } from 'react'
import {GlobalState} from '../../../GlobalState'
import axios from 'axios'
function Categories() {
 const state = useContext(GlobalState)
 const [categories , setCategories] = state.categoriesAPI.categories;
 const [category , setCategory] = useState('')
 const [token] = state.token
 const [callback , setCallback] = state.categoriesAPI.callback
 const [onEdit , setOnEdit] = useState(false)
 const [id , setID] = useState('')


 const createCategory= async e =>{
  e.preventDefault()
   try {
            if(onEdit){
                const res = await axios.put(`/api/category/${id}`, {name: category}, {
                    headers: {Authorization: token}
                })
                alert(res.data.msg)
            }else{
                const res = await axios.post('/api/category', {name: category}, {
                    headers: {Authorization: token}
                })
                alert(res.data.msg)
            }
            setOnEdit(false)
            setCategory('')
            setCallback(!callback)
            
        } catch (err) {
            alert(err.response.data.msg)
        }
 }

 const editCategory=async(id,name)=>{
   setID(id)
   setCategory(name)
   setOnEdit(true)
 }

 const deleteCategory=async id=>{
   try {
    const res = await axios.delete('/api/category')
   } catch (error) {
    alert(error.response.data.msg)
   }
 }
 return (
  <div className="categories" style={{textAlign:'center'}}>
   <form action="" onSubmit={createCategory}>
    <label htmlFor="category">Category</label>
    <input type="text" name="category" value={category} onChange={e=>setCategory(e.target.value)} required/>

    <button type="submit">{onEdit ? "Update" : "Save"}</button>
   </form>

   <div className="col" style={{textAlign:'center'}}>
    {
     categories.map(category=>(
      <div className="row" key={category._id}>
       <p>{category.name}</p>
       <div>
        <button onClick={()=>{
         editCategory(category._id , category.name)
        }}>Edit</button>
        <button onClick={()=>deleteCategory(category._id)}>Delete</button>

       </div>

      </div>
 ))
    }
   </div>
  </div>
 
 )
}

export default Categories
