import React, { useState } from 'react'
import {Link} from 'react-router-dom';
import axios from 'axios'
function Login() {
 const [user , setUser] = useState({
  email : "",
  password : ""
 })

 const onChangeInput = e=>{
  const {name,value} = e.target;
  setUser({...user,[name]:value})
 }

 const loginSubmit=async e=>{
  e.preventDefault()
  try {
   await axios.post("/user/login",{...user})

   localStorage.setItem("firstLogin", true)

   window.location.href = "/"
  } catch (error) {
   alert(error.response.data.msg)
  }
 }
 return (
  <div className="row">
  <div className="login-page column" style={{textAlign:'center'}}>
   <form action="" onSubmit={loginSubmit}>
    <input type="email" name="email" required placeholder="Email" value={user.email} onChange={onChangeInput}/>

    <input type="password" name="password" required placeholder="Password" autoComplete="on"  value={user.password} onChange={onChangeInput}/>

    <div className="row">
     <button type="submit">Login</button>
     <Link to="/register">Register</Link>
    </div>
   </form>
  </div>
  <div className="column">
   <img src="https://youmatter.world/app/uploads/sites/2/2019/01/globalization-definition-benefits-effects-examples.jpg" alt=""/>
  </div>
  </div>
 )
}

export default Login
