import React, { useState } from 'react'

function Deals() {
 const [input , output] = useState('');
 const [name , setName] = useState('Buying')
 const changeCol=()=>{
  let color = "#0071DF";
  output(color);
  let txt = "Buying";
  setName(txt)
  
 }
 const changeCol1=()=>{
  let color = "#0071DF";
  output(color);
  let txt = "Selling";
  setName(txt)
  
 }
 return (
  <div >
   <button type="submit" className="button-adj"
   onClick={changeCol} style={{backgroundColor:input , color:name}}>{name}</button>
   <button type="submit" className="button-adj1" onClick={changeCol1}>Selling</button>
  </div>
 )
}

export default Deals
