import React, { useContext, useEffect, useState } from 'react'
import axios from 'axios'
import {GlobalState} from '../../../GlobalState'
import Loading from '../utils/loading/Loading'
import {useHistory, useParams} from 'react-router-dom'
import { FaSave } from 'react-icons/fa'

const initialState={
 product_id : '',
 title : '',
 price : 0,
 description : "WorldRef ecommer Web app",
 content : 'Reactjs Framescript',
 category: '',
 _id : ''
}

function CreateProduct() {
 const state = useContext(GlobalState)
 const [product,setProduct] = useState(initialState)
 const [categories] = state.categoriesAPI.categories
 const [images ,setImages] = useState(false)
 const [loading , setLoading] = useState(false)


 const [isAdmin] = state.UserAPI.isAdmin
 const [token] = state.token

 const history = useHistory()
 const param = useParams()

 const [products] = state.productsAPI.products
 const [onEdit , setOnEdit] = useState(false)

 useEffect(()=>{
  if(param.id){
    products.forEach(product=>{
      if(product._id === param.id) 
      {
        setProduct(product)
        setImages(product.images)
      }

    })
    setOnEdit(true)
  }else{
    setProduct(initialState)
    setImages(false)
  }
 },[param.id , products])


  const handleDestroy=async()=>{
    try {
       if(!isAdmin) return alert("You are not an admin")
       setLoading(true)
       await axios.post('/api/destroy' , {public_id : images.public_id},{
         headers : {Authorization:token}
       })
       setLoading(false)
       setImages(false)
       history.push('/')
    } catch (error) {
      alert(error.response.data.msg)
    }
  }

 const handleUpload=async e=>{
   e.preventDefault()
   try {
     if(!isAdmin) return alert("You are not an admin")
     const file = e.target.files[0]
    //  console.log(file)

    if(!file) return alert("File not exist")

    if(file.size > 1024 *1024)
    return alert("Size too large!")

    if(file.type !== "image/jpeg" && file.type !== "image/png")
    return alert("File format is incorrect.")

    let formData = new FormData()
    formData.append('file',file)

    setLoading(true)
    const res = await axios.post('/api/upload',
    formData,{
        headers : {'content-type' : 'multipart/form-data',Authorization :token}
    })
      setLoading(false)
    // console.log(res)
    setImages(res.data)

   } catch (error) {
     alert(error.response.data.msg)
   }
 }

const handleChangeInput = e=>{
  const {name , value} = e.target
  setProduct({...product , [name]:value})
}

const handleSubmit = async e=>{
  e.preventDefault()
  try {
     if(!isAdmin) return alert("You are not an admin")
      if(!images) return alert("No images upload")

      await axios.post('/api/products',{...product,images},{
        headers:{Authorization:token}
      })

      setImages(false)
      setProduct(initialState)
      history.push('/')
  } catch (error) {
    alert(error.response.data.msg)
  }
}

  const styleUpload={
   display: images ? "block" : "none"
 }
 return (
  <div  className="create_product">
   <div className="upload">
    <input type="file" name="file" id="file_up" onChange={handleUpload}/>
      {
        loading ?  <div id="file_img"><Loading/></div>
        :<div id="file_img" style={styleUpload}>
     <img src={images ? images.url : ''} alt=""/>
     <span onClick={handleDestroy}>X</span>
    </div>
      }
   </div>

   <form action="" onSubmit={handleSubmit}>
    <div className="row">
     <label htmlFor="product_id">Product ID</label>
     <input type="text" name="product_id" id="product_id" required value={product.product_id} onChange={handleChangeInput} disabled = {product._id}/>
    </div>

    <div className="row">
     <label htmlFor="title">Title</label>
     <input type="text" name="title" id="title" required value={product.title} onChange={handleChangeInput}/>
    </div>

    <div className="row">
     <label htmlFor="price">Price</label>
     <input type="text" name="price" id="price" required value={product.price} onChange={handleChangeInput}/>
    </div>

    <div className="row">
     <label htmlFor="description">Description</label>
     <textarea type="text" name="description" id="description" required value={product.description} rows="5" onChange={handleChangeInput}/>
    </div>

    <div className="row">
     <label htmlFor="content">Content</label>
     <textarea type="text" name="content" id="content" required value={product.content} rows="7" onChange={handleChangeInput}/>
    </div>

    <div className="row">
     <label htmlFor="categories">Categories</label>
     <select type="text" name="category" id="categories" required value={product.category } onChange={handleChangeInput}>
      <option value="">Please select a category</option>
      {
       categories.map(category=>(
         <option value={category._id} key={category._id}>
          {category.name}
         </option>
       ))
      }
     </select>
    </div>
    <button type="submit"><FaSave/>Create</button>
   </form>
  </div>
 )
}

export default CreateProduct
